import React from 'react';
import ReactDOM from 'react-dom';
import mobilePic from './images/Untitled.png';
const first = (
  <h1>SUBSCRIBE</h1>
)

const firstNameVal = 'First name'
const lastNameVal = 'Last name'
const emailVal = 'Email'
const day = new Date()
const fullDay = day.getUTCDate()
const headerStyle= {
  backgroundColor: '#61DBFB',
  fontFamily: 'Roboto',
  padding: 25,
  lineHeight: 1.5,
  borderRadius: '10px 10px 10px 10px'
}

const h1Style= {
  textAlign: 'center'
}



const Header = () =>  (
  <header>
    <img style={{height: '200px', width:'200px', borderRadius: '100px 100px 100px 100px'}} src={mobilePic} alt='pic'/>
    <div className='nameProfile'>
      <h3>DO DUC TRUNG</h3>
      <i style={{marginLeft: '20px', paddingTop: '20px'}} className='fas fa-user-check'></i>
    </div>
    <p>Software Engineering, HCM</p>
    <h3>SKILLS</h3>
    <div className='skill-tag'>
      <button>HTML</button>
      <button>CSS</button>
      <button>Javascript</button>
      <button>Docker</button>
      <button>SASS</button>
      <button>NodeJS</button>
      <button>{fullDay}</button>

    </div>
  </header>
)

const Main = () => {
  <div className="main-content">Show your word</div>
}

const Footer = () => {
  <div className="footer" style={{textAlgin: 'center'}}>Copyright 2021</div>
}
const show = document.getElementById('root')


const App = () => {
  <div className="app">
    <Header />
    <Main />
    <Footer />
  </div>
}
ReactDOM.render(
  <Header />, show
);
